import React from 'react';
import { Container, Spinner, Toast, Button } from 'react-bootstrap';

class LandingPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            user: null,
        }

        fetch(
            `http://0.0.0.0:8000/get_user_information`, {
                headers: {
                    'authorization': `Bearer ${this.props.token.value}`
                }
            }
            ).then(
                res => {
                    console.log(res);
                    switch (res.status) {
                        case 200:
                            res.json().then(data => this.setState({user: data}))
                            break;
                        case 404, 401:
                            this.props.onDisconect();
                            break;
                    }
                }
            )
    }

    render() {
        if (!!this.state.user) {
            return (
                <Container>
                    <Button>Modify your contact informations</Button>
                    <Toast>
                        <Toast.Header>
                            <img src="public/logo192.png" className="rounded mr-2" alt="" />
                            <strong className="mr-auto">Logged in</strong>
                        </Toast.Header>
                        <Toast.Body>Welcome {this.state.user.login}!</Toast.Body>
                    </Toast>
                </Container>
            )    
        } else {
            return (
                <Container className="d-flex min-vh-100 justify-content-center align-items-center">
                    <Spinner animation="grow"/>
                </Container>
            )    
        }
    }

}

export default LandingPage;
