class AuthenticationPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            login: null,
            password: null
        }
    }

    handleSubmit() {
        console.log("that's it, we're sailing.");
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <label>
                    Login:
                    <input>{this.state.login}</input>
                </label>

                <label>
                    Password:
                    <input>{this.state.password}</input>
                </label>
            </form>
        );
    }
}

export default AuthenticationPanel;
