import React from 'react';

import { Container, Collapse, Col, Row } from 'react-bootstrap';
import './App.css';

import AuthenticationPanel from './authentication/authentication';
import RegistrationPanel from './authentication/registration';


function LandingPage({isLoggedIn, handleSuccessfulLogin}) {
  if (isLoggedIn) {
    return (
      <Container className="d-flex min-vh-100 justify-content-center align-items-center">
        <Col md={4}>
          <AuthenticationPanel onSuccessfulLogin={handleSuccessfulLogin}/>
        </Col>
        <Col md={{span: 4, offset: 4}}>
          <RegistrationPanel onSuccessfulLogin={handleSuccessfulLogin}/>
        </Col>
      </Container>
    )
  } else {
    return (
      <Container className="d-flex min-vh-100 justify-content-center align-items-center">
      </Container>
    )
  }
}

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {token:null}
    this.handleSuccessfulLogin = this.handleSuccessfulLogin.bind(this)
  }

  handleSuccessfulLogin(token) {
    this.setState({token})
  }

  render() {
    return (
      <LandingPage isLoggedIn={!this.state.token} onSuccessfulLogin={this.handleSuccessfulLogin}/>
    )
  }
}

export default App;
