import React from 'react';

class SettingsPanel extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            email: this.props.user.email
        }
    }

    handleInputChange(event) {
        const {name, value} = event.target;

        this.setState({[name]: value});
    }

    handleSave() {
        fetch(
            `http://0.0.0.0:8000/updateSettings`, {
                method: "POST",
                headers: {
                    'authorization': `Bearer ${this.props.token.value}`
                },
                body: JSON.stringify({
                    email: this.state.email,
                })
            }
        ).then(
            (response) => {
                switch (response.status) {
                    case 200:
                        this.props.onHide({shouldRefreshUser: true});
                        break;
                }
            }
        )
    }

    render() {
        return (
            <Modal
            show={this.props.show}
            onHide={this.props.onHide}
            backdrop="static"
            keyboard={false}
                >
                <Modal.Header closeButton>
                    <Modal.Title>Contact Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group controlId="login">
                            <Form.Label>Email</Form.Label>
                            <Form.Control name='email' type="text" placeholder="Enter new mail address" onChange={this.handleInputChange}>
                                {this.props.user.email}
                            </Form.Control>
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="primary" onClick={this.handleSave}>Save</Button>
                <Button variant="secondary" onClick={this.props.onHide}>
                    Cancel
                </Button>
                </Modal.Footer>
            </Modal>
        )
    }
}

export default SettingsPanel
