import React from 'react';

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class AuthenticationPanel extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            login: null,
            password: null
        }

        console.log(props);
        this.handleLogin = this.handleLogin.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    handleLogin() {
        console.log(`that's it, we're sailing. (${this.state.login}:${this.state.password})`);
    }

    handleInputChange(event) {
        const {name, value} = event.target;

        this.setState({[name]: value});
    }

    render() {
        return (
            <Form>
                <Form.Group controlId="login">
                    <Form.Label>Login</Form.Label>
                    <Form.Control name='login' type="text" placeholder="Enter Login" onChange={this.handleInputChange} />
                </Form.Group>

                <Form.Group controlId="login">
                    <Form.Label>Password</Form.Label>
                    <Form.Control name='password' type="password" onChange={this.handleInputChange}/>
                </Form.Group>

                <Button onClick={this.handleLogin} variant='primary'> Entering the Matrix!</Button>
            </Form>
        );
    }
}

export default AuthenticationPanel;
