import React from 'react';
import { Container, Spinner, Toast, Button, Jumbotron, Row } from 'react-bootstrap';

class LandingPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            user: null,
        }

        fetch(
            `http://0.0.0.0:8000/get_user_information`, {
                headers: {
                    'authorization': `Bearer ${this.props.token.value}`
                }
            }
            ).then(
                res => {
                    console.log(res);
                    switch (res.status) {
                        case 200:
                            res.json().then(data => this.setState({user: data}))
                            break;
                        case 404, 401:
                            this.props.onDisconect();
                            break;
                    }
                }
            )
    }

    render() {
        if (!!this.state.user) {
            return (
                <Container>
                    <Jumbotron>
                        <h1>Hello {this.state.user.login}</h1>
                        <p>Happy to see you back, at the moment you may modify your mail, more soon maybe?</p>
                        <Row>
                            <Button variant="success" onClick={this.handleOpenSettings}>Modify your contact informations</Button>
                            <Button variant="success" onClick={this.handleOpenSettings}>Modify your contact informations</Button>
                        </Row>
                    </Jumbotron>
                </Container>
            )    
        } else {
            return (
                <Container className="d-flex min-vh-100 justify-content-center align-items-center">
                    <Spinner animation="grow"/>
                </Container>
            )    
        }
    }

}

export default LandingPage;
